def find_max(lst):
    m = lst[0]
    for x in lst:
        if x > m:
            m = x
    return m

def list_sum(values):
    total = 0
    for v in values:
        total += v
    return total

def count_vowels(s):
    vowels = 'aeiouAEIOU'
    return sum(1 for c in s if c in vowels)
